import { Component, OnInit } from '@angular/core';
import { KorisnikService } from '../korisnik.service';
import { Router } from '@angular/router';
import { Korisnik } from '../models/korisnik';
import { HttpClient, HttpEventType, HttpResponse } from '@angular/common/http';
import { Observable } from 'rxjs';
import * as sha512 from 'js-sha512';
import { AppComponent } from '../app.component';

@Component({
  selector: 'app-registracija-agencija',
  templateUrl: './registracija-agencija.component.html',
  styleUrls: ['./registracija-agencija.component.css']
})
export class RegistracijaAgencijaComponent implements OnInit {

  uploadedFiles: Array < File > ;
  imageSrc: string;
  img_w:number;
  img_h:number;
  img_err:boolean =false;
  filename_server : string = "";
  slanje_u_toku : boolean = false;

  fileChange(element) {
    this.uploadedFiles = element.target.files;


    let event = element;
    let reader = new FileReader();

    if(event.target.files && event.target.files.length) {
      const [file] = event.target.files;
      reader.readAsDataURL(file);

      reader.onload = () => {

        this.imageSrc = reader.result as string;


      };

      let img = new Image()
      img.src = window.URL.createObjectURL(event.target.files[0])
      img.onload = () => {
        this.img_w = img.width;
        this.img_h = img.height;
        let val = (this.img_h > 300 ||this.img_h <100 || this.img_w <100 || this.img_w >300 ) 
        this.greska =val? "слика није одговарајуће величоне" : null
        if(val){
          this.imageSrc = null;
          this.uploadedFiles=null;
          this.img_err = true;
        }else{
          this.img_err = false;

        }
      }

    }




  }



async upload() {


}


  constructor(private korisnikService: KorisnikService, private router: Router, private http: HttpClient) { }

  ngOnInit(): void {

    this.korisnikService.sviKorisnici().subscribe((kIzBaze: Korisnik[])=>{
      this.korisnici = kIzBaze;
    })

    //fu


  }
  postoji_k_ime: boolean = false;
  postoji_mejl: boolean = false;
  k_ime: string;
  lozinka: string;
  lozinka_opet: string;
  lozinka_7:boolean = true;
  lozinka_12:boolean=true;
  lozinka_vs:boolean=true;
  lozinka_broj:boolean=true;
  lozinka_sk:boolean = true;
  lozinka_pocinje_slovom:boolean = true;
  lozinke_iste:boolean = true;
  mejl:string;
  ime:string;
  adresa:string;
  telefon: number;
  korisnici : Korisnik[]
  greska:string;
  poruka_o_stanju:string;
  odbrojavanje:number;
  maticni:number;
  opis: string;
  check() {

    let set_in_iter=false;
    let set_in_iter_mejl = false;

      if(!this.korisnici) return;
      this.korisnici.forEach( (clan)=>{
        if(clan.korisnicko_ime==this.k_ime) {
          this.postoji_k_ime=true;
          set_in_iter=true;
        }
        else if(!set_in_iter) this.postoji_k_ime=false;
        if(clan.imejl==this.mejl){
          this.postoji_mejl = true;
          set_in_iter_mejl = true;
        }
        else if(!set_in_iter_mejl)  this.postoji_mejl  = false;

    }
    )
    return ;
  }
  format_mejl(){
    return /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(this.mejl);
  }
  check_pass(){
    if(this.lozinka==null || this.lozinka.length==0) {
      this.lozinka_7 = true;
      this.lozinka_12 = true;
      this.lozinka_vs = true;
      this.lozinka_broj = true;
      this.lozinka_sk = true;
      this.lozinka_pocinje_slovom = true;
      return;
    };
    const format = /[ `!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?~]/;
    if(this.lozinka.search(/[A-Z]/)<0) this.lozinka_vs = false;
    this.lozinka_vs = true;
    if(this.lozinka.length<7) this.lozinka_7 = false;
    else this.lozinka_7 = true;
    if(this.lozinka.length>12) this.lozinka_12 = false;
    else this.lozinka_12 = true;
    if(!/\d/.test(this.lozinka)) this.lozinka_broj = false;
    else this.lozinka_broj = true;
    if(!format.test(this.lozinka)) this.lozinka_sk = false;
    else this.lozinka_sk = true;
    if(!/[A-Za-z]/.test(this.lozinka.charAt(0))) this.lozinka_pocinje_slovom = false;
    else this.lozinka_pocinje_slovom = true;
    if(!/[A-Z]/.test(this.lozinka)) this.lozinka_vs = false;
    else this.lozinka_vs = true;

  }
  check_pass_opet(){
    if(this.lozinka==null) {
      this.lozinka_7 = true;
      this.lozinka_12 = true;
      this.lozinka_vs = true;
      this.lozinka_broj = true;
      this.lozinka_sk = true;
      this.lozinka_pocinje_slovom = true;
